mapper.select:
SELECT

FROM bs_hepatitis_bcheckin a  



		AND a.username = #{username}


		AND a.idcard_no = #{idcardNO}


		AND a.id = #{id}


		AND a.sex = #{sex}


		AND a.birthday = #{birthday}


		AND a.age = #{age}


		AND a.link_phone = #{linkPhone}


		AND a.vaccine_name = #{vaccineName}


		AND a.standard = #{standard}


		AND a.manufacturer = #{manufacturer}


		AND a.batch = #{batch}


		AND a.dosage = #{dosage}


		AND a.create_date = #{createDate}


		AND a.create_date between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')


		AND a.vacctype = #{vaccType}

	and a.deflag='0' 
	and a.localcode = #{localCode}



		ORDER BY ${page.orderBy}


		ORDER BY
			a.vacctype,
		    a.idcard_no DESC,
			a.create_date DESC

SELECT 

FROM bs_hepatitis_bcheckin a


	a.deflag='0' and a.localcode = #{localCode}



		ORDER BY ${page.orderBy}

mapper.update:
update bs_hepatitis_bcheckin 
set deflag = '1'
WHERE id = #{id}

mapper.select:
SELECT
	*
FROM
	BS_HEPATITIS_BCHECKIN c

		(c.username = #{searchName}
	OR  c.link_phone = #{searchName}
	OR  c.idcard_no = #{searchName}
	OR  c.hepa_b_code LIKE '%' || #{searchName} || '%'
	OR  to_char(c.birthday,'yyyyMMdd') = #{searchName})

		AND c.birthday = #{birthday}


		AND c.create_date = #{createDate}


		AND c.link_phone = #{linkPhone}

	and c.deflag='0' 
	and c.localcode = #{localCode}

SELECT
	vaccname
FROM
	SYS_VACC_INFO


		AND "ID" = #{vaccType}

	and localcode = #{localCode}

SELECT
	*
FROM
	SYS_VACC_INFO_PZ


		AND "VID" = #{vaccType}


		AND "COUNT" = #{count}

	and "STATUS" = #{status}
	and localcode = #{localCode}

SELECT * FROM SYS_VACC_INFO
AND "ID" = #{vaccType}
AND type = '2' and localcode = #{localCode}

SELECT NVL ( LPAD ( MAX (SUBSTR(T .hepa_b_code, 16, 4)) + 1, 4, 0 ), '0001' ) AS max_code
FROM
	bs_hepatitis_bcheckin T
WHERE
	SUBSTR (T.hepa_b_code, 12, 4) = #{year}
AND SUBSTR (T.hepa_b_code, 20) = #{vaccType}
AND T.localcode = #{localCode}

SELECT p .vaccine_id AS vaccineId, p .batch AS batch, p .standard AS "standard", "COUNT" (batch) AS storeNum1 FROM bs_hepatitis_b_num p
AND p.vacctype = #{vaccType}
AND p .pay_status = '0' AND p .status = '0' AND p .w_status = '0' AND p .deflag = '0' and p.localcode = #{localCode}
GROUP BY p .vaccine_id, p .batch, p .standard

SELECT p .vaccine_id AS vaccineId, p .batch AS batch, p .standard AS "standard", "COUNT" (batch) AS storeNum2 FROM bs_hepatitis_b_num p
AND p.vacctype = #{vaccType}
AND p .pay_status = '1' AND p .status = '0' AND p .w_status = '0' AND p .deflag = '0' and p.localcode = #{localCode}
GROUP BY p .vaccine_id, p .batch, p .standard

SELECT p .vaccine_id AS vaccineId, p .batch AS batch, p .standard AS "standard", "COUNT" (batch) AS storeNum3 FROM bs_hepatitis_b_num p
AND p.vacctype = #{vaccType}
AND p .status = '1' AND p .w_status = '0' AND p .deflag = '0' and p.localcode = #{localCode}
GROUP BY p .vaccine_id, p .batch, p .standard

SELECT
	"COUNT"(*)
FROM
	BS_HEPATITIS_BCHECKIN A


		 AND a.create_date between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss') 


		AND	A.create_by  = #{createById}


		AND	a.vacctype  = #{vaccType}

	and a.deflag='0' and a.localcode = #{localCode}

SELECT "COUNT"(*) FROM BS_HEPATITIS_BCHECKIN A LEFT JOIN BS_HEPATITIS_B_NUM R ON R.check_id = A.id AND R.localcode = A.localcode
AND R .real_date between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND R.create_by = #{createById}
AND R.vacctype = #{vaccType}
AND R .real_date IS NOT NULL AND R .status = '1' AND R .w_status = '0' AND R.deflag='0' AND a.localcode = #{localCode}
