mapper.select:
SELECT 

FROM bs_hepatitis_b_num a


	and a.deflag = '0' 
	and a.localcode = #{localCode}



		ORDER BY ${page.orderBy}

SELECT

FROM bs_hepatitis_b_num a



		AND a.check_id = #{checkId}

	AND a.n_status = '0'
	AND a.deflag = '0'
	AND a.w_status = '0'
	AND a.status != '1'
	and a.localcode = #{localCode}



		ORDER BY ${page.orderBy}


	ORDER BY a.vaccine_num

SELECT * FROM( SELECT "vaccine_name", "manufacturer", "standard", "COUNT"("standard") as "vcount" FROM ( SELECT bp.manufacturer AS "manufacturer", bp.vacc_name AS "vaccine_name", a.standard AS "standard" FROM bs_hepatitis_b_num A LEFT JOIN VIEW_BS_PRODUCT_NO bp ON bp.vaccineid = A .vaccine_id and bp.localcode = A .localcode AND bp.batchno = A .batch
and a.localcode = #{localCode}
AND a.real_date between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a.create_by = #{createById}
AND a.vacctype = #{vaccType}
AND A .real_date IS NOT NULL AND A .status = '1' AND a.w_status = '0' and a.deflag='0' and bp.officecode = #{officeCode}
ORDER BY A .vaccine_num ASC ) GROUP BY "vaccine_name", "manufacturer", "standard" )ORDER BY "vaccine_name" DESC

SELECT * FROM( SELECT "vaccine_num", "manufacturer", "COUNT"("manufacturer") as "vcount" FROM ( SELECT a.vaccine_num AS "vaccine_num", bp.manufacturer AS "manufacturer" FROM bs_hepatitis_b_num A LEFT JOIN VIEW_BS_PRODUCT_NO bp ON bp.vaccineid = A .vaccine_id and bp.localcode = A .localcode AND bp.batchno = A .batch
and a.localcode = #{localCode}
AND a.real_date between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a.create_by = #{createById}
AND a.vacctype = #{vaccType}
AND A .real_date IS NOT NULL AND A .status = '1' AND a.w_status = '0' and a.deflag='0' and bp.officecode = #{officeCode}
ORDER BY A .vaccine_num ASC ) GROUP BY "vaccine_num", "manufacturer" )ORDER BY "manufacturer", "vaccine_num" DESC

SELECT b.id AS "kin.id", b.username AS "kin.username", b.idcard_no AS "kin.idcardNo", b.sex AS "kin.sex", b.birthday AS "kin.birthday", b.age AS "kin.age", b.link_phone AS "kin.linkPhone", substr(b.hepa_b_code,14) as "kin.hepaBcode",
FROM bs_hepatitis_b_num a LEFT JOIN bs_hepatitis_bcheckin b ON b.id = a.check_id and b.localcode = a.localcode LEFT JOIN SYS_USER su ON su."ID" = a.create_by
and a.localcode = #{localCode}
AND b.username = #{username}
AND b.idcard_no = #{idcardNo}
AND a.vaccine_num = #{vaccineNum}
AND a.manufacturer = #{manufacturer}
AND a.create_by = #{createById}
AND a.vacctype = #{vaccType}
AND a.real_date between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a.real_date IS NOT NULL AND a.status = '1' AND a.w_status = '0' and a.deflag='0'
ORDER BY ${page.orderBy}
ORDER BY a.manufacturer ASC , b.hepa_b_code ASC

SELECT p .vaccine_id AS "vaccineId", p .batch AS "batch", p .standard AS "standard", "COUNT" (batch) AS "storeNum2" FROM bs_hepatitis_b_num p
and p.localcode = #{localCode}
AND p .create_date between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND p .create_by = #{createById}
AND p.vacctype = #{vaccType}
AND p .pay_status = '1' AND p.status = '0' AND p .w_status = '0' AND p .deflag = '0'
GROUP BY p .vaccine_id, p .batch, p .standard

SELECT
FROM bs_hepatitis_b_num a
WHERE a.check_id = #{checkId} AND a.n_status = '1' AND a.real_date IS NOT NULL AND a.status = '1' AND a.deflag='0' and a.localcode = #{localCode}

SELECT a.id FROM bs_hepatitis_b_num a
(a.signature = '0' OR a.signature = '2') and a.localcode = #{localCode} AND a.w_status = '0' AND a.n_status = '0' AND a.deflag = '0'
AND a.check_id = #{checkId}

