mapper.select:
SELECT 

FROM sys_holiday a

WHERE a.id = #{id}

SELECT 

FROM sys_holiday a


	a.del_flag = #{DEL_FLAG_NORMAL} 
	AND a.LOCALCODE = #{localCode}

		AND a.date_type = #{dateType}


		AND a.remarks like '%' || #{remarks} || '%'


		AND a.date_time > #{dateTimeAfter}


		AND a.date_time < #{dateTimeBefore}




		ORDER BY ${page.orderBy}


		ORDER BY a.DATE_TYPE DESC , a.DATE_DAY,  a.update_date DESC

SELECT 

FROM sys_holiday a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.DATE_TYPE DESC , a.DATE_DAY,  a.update_date DESC

mapper.update:
UPDATE sys_holiday SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
