SELECT

FROM bs_rabies_num a


		a."deflag" = '0' 
	and a.localcode = #{localCode}



		ORDER BY ${page.orderBy}

SELECT

FROM bs_rabies_num a


    and a.localcode = #{localCode}

		AND a."checkid" = #{checkid}


		AND a."vaccinum" = #{vaccinum}


		AND trunc(a."vaccidate") = trunc(#{vaccidate})

	AND a."nstatus" = '0'
	AND a."deflag" = '0'
	AND (a.status != '1' OR a."realdate" IS NULL)

SELECT

FROM bs_rabies_num a


    and a.localcode = #{localCode}

		AND a."checkid" = #{checkid}

	AND a."nstatus" = '0'
	AND a."deflag" = '0'
	AND a."wstatus" = '0'
	AND a.status != '1'



		ORDER BY ${page.orderBy}


	ORDER BY a."vaccinum"

SELECT b."id" AS "kin.id", b."username" AS "kin.username", b."card" AS "kin.card", b."sex" AS "kin.sex", b."birthday" AS "kin.birthday", b."age" AS "kin.age", b."linkphone" AS "kin.linkphone", b."exposelevel" AS "kin.exposelevel", substr(b."rabiescode",14) AS "kin.rabiescode",
FROM bs_rabies_num a LEFT JOIN bs_rabies_checkin b ON b."id" = a."checkid" and b.localcode = a.localcode LEFT JOIN SYS_USER su ON su."ID" = a."create_by"
and a.localcode = #{localCode}
AND b."username" = #{kin.username}
AND b."card" = #{kin.card}
AND b."exposelevel" = #{kin.exposelevel}
AND a."manufacturer" = #{manufacturer}
AND a."create_by" = #{createById}
AND a."realdate" between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a."vaccinum" != '0' AND a."realdate" IS NOT NULL AND a.status = '1' AND a."wstatus" = '0' AND a."deflag" = '0'
ORDER BY ${page.orderBy}
ORDER BY a."manufacturer" ASC, b."rabiescode" ASC

SELECT DISTINCT ttt.*, fff."vaccinum" FROM (SELECT b."username" AS "kin.username", b."sex" AS "kin.sex", SUBSTR(b."rabiescode", 14) AS "kin.rabiescode", A ."manufacturer" AS "manufacturer", A ."create_name" AS "createByName" FROM bs_rabies_num A LEFT JOIN bs_rabies_checkin b ON b."id" = A."checkid" and b.localcode = A.localcode LEFT JOIN SYS_USER su ON su."ID" = A."create_by"
and a.localcode = #{localCode}
AND b."username" = #{kin.username}
AND b."card" = #{kin.card}
AND b."exposelevel" = #{kin.exposelevel}
AND a."manufacturer" = #{manufacturer}
AND a."create_by" = #{createById}
AND a."realdate" between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a."vaccinum" = '0' AND a."realdate" IS NOT NULL AND a.status = '1' AND a."wstatus" = '0' AND a."deflag" = '0'
ORDER BY A ."manufacturer" ASC, b."rabiescode" ASC) TTT LEFT JOIN ( SELECT COUNT(b."rabiescode") AS "vaccinum", SUBSTR(b."rabiescode", 14) AS "kin.rabiescode" FROM bs_rabies_num A LEFT JOIN bs_rabies_checkin b ON b."id" = A."checkid" and b.localcode = A.localcode LEFT JOIN SYS_USER su ON su."ID" = A."create_by"
and a.localcode = #{localCode}
AND b."username" = #{kin.username}
AND b."card" = #{kin.card}
AND b."exposelevel" = #{kin.exposelevel}
AND a."manufacturer" = #{manufacturer}
AND a."create_by" = #{createById}
AND a."realdate" between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a."vaccinum" = '0' AND a."realdate" IS NOT NULL AND a.status = '1' AND a."wstatus" = '0' AND a."deflag" = '0'
GROUP BY b."rabiescode") FFF On ttt."kin.rabiescode" = fff."kin.rabiescode" ORDER BY ttt."manufacturer" ASC, ttt."kin.rabiescode"

SELECT P ."vaccineid" AS "vaccid", P ."batchnum" AS "batchnum", P ."dose" AS "standard", "COUNT" ("batchnum") AS "storenum2" FROM BS_RABIES_NUM P
AND P."create_date" between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND P."create_by" = #{createById}
AND P ."paystatus" = '1' AND P .status = '0' AND P ."wstatus" = '0' AND P ."deflag" = '0' and p.localcode = #{localCode}
GROUP BY P ."vaccineid", P ."batchnum", P ."dose"

SELECT * FROM( SELECT "vaccname", "manufacturer", "standard", "COUNT"("manufacturer") as "vcount" FROM ( SELECT bp.manufacturer AS "manufacturer", bp.vacc_name AS "vaccname", a."dose" AS "standard" FROM bs_rabies_num A LEFT JOIN VIEW_BS_PRODUCT_NO bp ON bp.vaccineid = A ."vaccineid" and bp.localcode = A.localcode AND bp.batchno = A ."batchnum"
and a.localcode = #{localCode}
AND a."realdate" between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a."create_by" = #{createById}
AND A ."realdate" IS NOT NULL AND A .status = '1' AND a."wstatus" = '0' AND a."deflag" = '0' and bp.officecode = #{officeCode}
ORDER BY A ."vaccinum" ASC ) GROUP BY "vaccname", "manufacturer", "standard" )ORDER BY "vaccname" DESC

SELECT * FROM( SELECT "vaccinum", "manufacturer", "COUNT"("manufacturer") as "vcount" FROM ( SELECT a."vaccinum" AS "vaccinum", bp.manufacturer AS "manufacturer" FROM bs_rabies_num A LEFT JOIN VIEW_BS_PRODUCT_NO bp ON bp.vaccineid = A ."vaccineid" and bp.localcode = A .localcode AND bp.batchno = A ."batchnum"
and a.localcode = #{localCode}
AND a."realdate" between to_date('${begin}','yyyy-MM-dd HH24:mi:ss') and to_date('${end}','yyyy-MM-dd HH24:mi:ss')
AND a."create_by" = #{createById}
AND A ."realdate" IS NOT NULL AND A .status = '1' AND a."wstatus" = '0' AND a."deflag" = '0' and bp.officecode = #{officeCode}
ORDER BY A ."vaccinum" ASC ) GROUP BY "vaccinum", "manufacturer" )ORDER BY "manufacturer", "vaccinum" DESC

